mapper.select:
select id, algorithm_name as algorithmName,  algorithm_description as algorithmDescription, algorithm_pro as algorithmPro, algorithm_con as algorithmCon,
material_num as materialNum, article_num as articleNum, system_num as systemNum, category_id as categoryId, 
create_tm as createTm, modify_tm as modifyTm from algorithm where category_id=#{id}

